use \php\Boot;

class StringTools {
	/**
	 * @param string $s
	 * @param string $c
	 * @param int $l
	 * 
	 * @return string
	 */
	static public function lpad ($s, $c, $l) {
		$tmp = null;
		if (strlen($c) !== 0) {
			$tmp = strlen($s) >= $l;
		} else {
			$tmp = true;
		}
		if ($tmp) {
			return $s;
		}
		return str_pad($s, (int)(ceil(($l - strlen($s)) / strlen($c))) * strlen($c) + strlen($s), $c, STR_PAD_LEFT);
	}
}


Boot::registerClass(StringTools::class, 'StringTools');
